import React from 'react';

export default function WinLossModal({ isOpen, message, onClose, onRestart }) {
  if (!isOpen) return null;
  return (
    <div style={{ position: 'fixed', left: 0, top: 0, right: 0, bottom: 0, background: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div style={{ background: 'white', padding: 20, borderRadius: 6, minWidth: 300 }}>
        <h2>{message}</h2>
        <div style={{ display: 'flex', justifyContent: 'flex-end', gap: 8 }}>
          <button onClick={onClose}>Close</button>
          <button onClick={onRestart}>Play again</button>
        </div>
      </div>
    </div>
  );
}
